components.buttons:
.btn-primary{border-radius:var(--radius);background-color:#3b82f6;padding:.75rem 1.5rem;font-weight:600;color:#ffffff;transition:all .3s cubic-bezier(.4,0,.2,1)}
.btn-primary:hover{transform:translateY(-.25rem);background-color:#2563eb;box-shadow:0 10px 30px #3b82f666}
.btn-outline{border-radius:var(--radius);border:2px solid #3b82f6;padding:.75rem 1.5rem;font-weight:600;color:#3b82f6;transition:all .3s cubic-bezier(.4,0,.2,1)}
.btn-outline:hover{transform:translateY(-.25rem);background-color:#3b82f6;color:#ffffff;box-shadow:0 10px 30px #3b82f666}
.btn-accent-soft{border-radius:.5rem;background:rgba(255,255,255,.2);padding:.5rem 1rem;font-size:.875rem;font-weight:500;display:inline-flex;align-items:center;gap:.5rem;color:#ffffff;transition:all .3s cubic-bezier(.4,0,.2,1)}
.btn-accent-soft:hover{transform:translateY(-.25rem);background:rgba(255,255,255,.32);box-shadow:0 10px 24px rgba(15,23,42,.25)}
.btn-accent-soft:active{transform:translateY(0)}
.btn-white-lift{border-radius:.5rem;background:#ffffff;padding:.75rem 1.5rem;font-weight:600;color:#3b82f6;display:inline-flex;align-items:center;gap:.5rem;transition:all .3s cubic-bezier(.4,0,.2,1)}
.btn-white-lift:hover{transform:translateY(-.25rem);background:#f8fafc;box-shadow:0 10px 24px rgba(15,23,42,.25)}
.btn-white-lift:active{transform:translateY(0)}
.floating-action-btn{position:fixed;width:3rem;height:3rem;border-radius:9999px;background:#3b82f6;color:#ffffff;display:flex;align-items:center;justify-content:center;box-shadow:0 10px 28px rgba(15,23,42,.24);transition:transform .3s cubic-bezier(.4,0,.2,1),background-color .3s cubic-bezier(.4,0,.2,1),box-shadow .3s cubic-bezier(.4,0,.2,1);z-index:40}
.floating-action-btn:hover{transform:translateY(-.2rem) scale(1.06);background:#2563eb;box-shadow:0 14px 34px rgba(15,23,42,.3)}
.floating-action-btn:active{transform:translateY(0) scale(1)}
.floating-action-btn:focus-visible{outline:2px solid #3b82f6;outline-offset:2px}
